mapper.insert:
-- -- each format has a component table which they can extend -- CREATE TABLE IF NOT EXISTS ${format}_component ( component_id INT GENERATED BY DEFAULT AS IDENTITY, repository_id INT NOT NULL, namespace VARCHAR NOT NULL, name VARCHAR NOT NULL, kind VARCHAR NOT NULL, version VARCHAR NOT NULL, attributes ${JSON_TYPE} NOT NULL, created TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP, last_updated TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
CONSTRAINT pk_${format}_component_id PRIMARY KEY (component_id), CONSTRAINT uk_${format}_component_coordinate UNIQUE (repository_id, namespace, name, version), CONSTRAINT fk_${format}_component_repository_id FOREIGN KEY (repository_id) REFERENCES ${format}_content_repository (repository_id) );
CREATE INDEX IF NOT EXISTS idx_${format}_component_kind ON ${format}_component (kind);
--

mapper.select:
SELECT * FROM ${format}_component WHERE repository_id = #{repositoryId}

  AND kind = #{kind}


  AND component_id > #{continuationToken}

ORDER BY component_id LIMIT #{limit};

mapper.delete:
DELETE FROM ${format}_component WHERE
     component_id IN (SELECT component_id FROM ${format}_component WHERE 
    repository_id = #{repositoryId}
     LIMIT #{limit});

mapper.update:
-- store purged component ids in a temporary table local to our session
CREATE LOCAL TEMPORARY TABLE IF NOT EXISTS purged_component_ids (component_id INT NOT NULL UNIQUE)
    NOT PERSISTENT;

mapper.delete:
INSERT INTO purged_component_ids (component_id)
    SELECT component_id FROM ${format}_asset
        WHERE repository_id = #{repositoryId} AND component_id IS NOT NULL
    GROUP BY component_id
        HAVING MAX(last_downloaded) <= CURRENT_TIMESTAMP - CAST(#{daysAgo} AS INT) * INTERVAL '1' DAY
     LIMIT #{limit};

DELETE FROM ${format}_asset WHERE component_id IN (SELECT component_id FROM purged_component_ids);
DELETE FROM ${format}_component WHERE component_id IN (SELECT component_id FROM purged_component_ids);

-- we want to return number of deleted components so we can't simply drop the table
-- (also means we can re-use the purge table for repeated calls in the same session)
DELETE FROM purged_component_ids;
